import { useState } from "react";
import ToDo from "./ToDo";
import ToDoForm from "./ToDoForm";

function App() {
  const [todos, setTodos] = useState([]);

  const addTask = (usInput) => {};
  const removeTask = () => {};
  const handleTogle = () => {};
  return (
    <div className="App">
      <header>
        <h1>ToDoListReact</h1>
        <h2 class="">Task list {todos.length}</h2>
      </header>
      <ToDoForm addTask={addTask} />
      {todos.map((todo) => {
        return (
          <ToDo
            todo={todo}
            key={todo.id}
            toggleTask={handleTogle}
            removeTask={removeTask}
          />
        );
      })}
    </div>
  );
}

export default App;
